console.log("js file of toggle_friendship is loaded");class ToggleFriendship{constructor(e){this.toggler=e,this.toggleFriendship()}toggleFriendship(){$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"GET",url:$(t).attr("href")}).done((function(e){1==e.data.friendAdded?$(" button",t).html("Remove Friend"):$(" button",t).html("Add Friend")})).fail((function(e){console.log("error in completing the request",e)}))}))}}new ToggleFriendship($(".toggle-friend-button"));