class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let l=parseInt($(e).attr("data-likes"));console.log(l),1==t.data.deleted?(l-=1,$(" .likes-count",e).html(`\n                        ${l}\n                        <i class="far fa-thumbs-up"></i> Likes \n                    `)):(l+=1,$(" .likes-count",e).html(`\n                        ${l}\n                        <i class="fas fa-thumbs-up"></i> Likes \n                    `)),$(e).attr("data-likes",l)})).fail((function(t){console.log("error in completing the request")}))}))}}