class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"POST",url:$(t).attr("href"),beforeSend:function(){$(t).parent().css({opacity:"0.7","pointer-events":"none"})}}).done((function(e){let n=parseInt($(t).attr("data-likes"));1==e.data.deleted?(n-=1,$(" .likes-count",t).html(`\n                        <span>${n}</span>\n                        <span>\n                            <i class="far fa-thumbs-up"></i>\n                            <span style="color: blue;">Likes</span>\n                        </span> \n                    `),$(" .likes-count",t).css({color:"grey"})):(n+=1,$(" .likes-count",t).html(`\n                        <span>${n}</span>\n                        <span>\n                            <i class="fas fa-thumbs-up animate__animated animate__heartBeat"></i>\n                            <span style="color: blue;">Likes</span>\n                        </span>\n                    `),$(" .likes-count",t).css({color:"blue"})),$(t).attr("data-likes",n),$(t).parent().css({opacity:"1","pointer-events":"auto"})})).fail((function(e){console.log("error in completing the request")}))}))}}