var animationElements=document.querySelectorAll("#welcome-container .animate__animated"),animationDone=[];for(let n=0;n<animationElements.length;n++)animationDone[n]=!1;function checkScroll(n,e){let t=n.getBoundingClientRect();if(!animationDone[e]&&t.top<=window.innerHeight){animationDone[e]=!0;var i=n.getAttribute("data-anination-name");n.classList.add(i)}else if(t.top>window.innerHeight){animationDone[e]=!1;i=n.getAttribute("data-anination-name");n.classList.remove(i)}}function fireAnimationOfAlreadyVisibleElements(){for(let n=0;n<animationElements.length;n++)checkScroll(animationElements[n],n)}document.getElementById("layout-main").addEventListener("scroll",(function(n){console.log("scrolled");for(let n=0;n<animationElements.length;n++)checkScroll(animationElements[n],n)})),fireAnimationOfAlreadyVisibleElements();